package com.munyu.wamis.domainimport javax.persistence.*@Entitydata class Connection(        @Column(nullable = false)        val meterSerialNumber: String,        @Column(nullable = false,columnDefinition = "TEXT")        val meterLocation: String) : AbstractAuditableEntity(){    @Column(nullable = false)    var connectionCode: String = ""    @Enumerated(EnumType.STRING)    @Column(nullable = true)    var status: ConnectionStatus? = null    @ManyToOne    @JoinColumn(name = "customer_id")    var customer: Customer? = null    init {        this.connectionCode = generateConnectionCode()        this.status = ConnectionStatus.INITIAL    }    constructor(serial: String,location: String,status: ConnectionStatus,code: String,customer: Customer ): this(serial,location){        this.status = status        this.customer = customer    }    fun generateConnectionCode(): String{        return (0..9).shuffled().subList(3,6).toString()    }}